import { useState } from "react";
function InputSlider(props) {
  const { iptId, sldId, iptStep, sldStep, dftValue } = props;
  const [value, setValue] = useState(parseFloat(dftValue));
  const updateSlider = (e) => {
    setValue(e.target.value);
  };
  const updateInput = (e) => {
    const iptVal = e.target.value;
    if (!isNaN(iptVal) && iptVal >= 0 && iptVal <=1) {
        setValue(iptVal);
    }
  };

  return (
    <div>
      <label for={iptId} style={{ justifySelf: "left" }}></label>
      <input
        type="number"
        id={iptId}
        min="0"
        max="1"
        step={iptStep}
        value={value}
        onChange={updateSlider}
      />
      <label for={sldId}>0</label>
      <input
        type="range"
        min="0"
        max="1"
        step={sldStep}
        value={value}
        disabled = "false"
        onChange={updateInput}
      />
      <label for={sldId}>1</label>
    </div>
  );
}

export default InputSlider;
